// src/pages/DashboardPage.js
import React, { useEffect, useMemo } from 'react';
import { useStudents } from '../hooks/useStudents';
import StatCard from '../components/dashboard/StatCard';
import { Users, BookOpen, CheckCircle, XCircle } from 'lucide-react';

const DashboardPage = () => {
    const { students, fetchStudents, pagination } = useStudents();
    
    useEffect(() => {
        // Fetch all students for accurate stats
        fetchStudents(1, { /* no filters */ });
    }, []);

    const stats = useMemo(() => {
        const totalStudents = pagination.total;
        const activeStudents = students.filter(s => s.status === 'active').length;
        const inactiveStudents = totalStudents - activeStudents;
        const avgGpa = students.reduce((acc, s) => acc + parseFloat(s.gpa), 0) / (students.length || 1);
        
        return { totalStudents, activeStudents, inactiveStudents, avgGpa };
    }, [students, pagination.total]);

    return (
        <div>
            <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-gray-200">Dashboard</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <StatCard title="Total Students" value={stats.totalStudents} icon={<Users />} />
                <StatCard title="Active Students" value={stats.activeStudents} icon={<CheckCircle />} color="green" />
                <StatCard title="Inactive Students" value={stats.inactiveStudents} icon={<XCircle />} color="red" />
                <StatCard title="Average GPA" value={stats.avgGpa.toFixed(2)} icon={<BookOpen />} color="blue" />
            </div>
            {/* Add more charts or data visualizations here */}
        </div>
    );
};

export default DashboardPage;
