// src/pages/LoginPage.js
import React, { useState } from 'react';
import { useAuth } from '../hooks/useAuth';
import { useNotifications } from '../hooks/useNotifications';
import LoginForm from '../components/auth/LoginForm';
import './LoginPage.css';

const LoginPage = () => {
  const { login } = useAuth();
  const { addNotification } = useNotifications();
  const [loading, setLoading] = useState(false);
  
  const handleLogin = async (credentials) => {
    setLoading(true);
    try {
      await login(credentials);
      addNotification({ type: 'success', message: 'Login successful! Redirecting...' });
      // The redirect is handled by the router which sees isAuthenticated is now true
    } catch (error) {
      const errorMessage = error.response?.data?.msg || 'Login failed. Please check your credentials.';
      addNotification({ type: 'error', message: errorMessage });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page-container">
      <h1 className="login-title">Login</h1>
      <LoginForm onLogin={handleLogin} loading={loading} />
    </div>
  );
};

export default LoginPage;
