// src/components/auth/LoginForm.js
import React, { useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useNotifications } from '../../hooks/useNotifications';

const LoginForm = () => {
  // ... (state and handlers remain the same)
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { login } = useAuth();
  const { addNotification } = useNotifications();
  
  const handleSubmit = async (e) => { /* ... same as before */ };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-200 dark:from-slate-900 dark:to-black flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white/70 dark:bg-slate-800/60 backdrop-blur-xl rounded-2xl shadow-2xl p-8 transition-all-300">
        <div className="text-center mb-8">
          <h2 className="text-4xl font-bold text-slate-800 dark:text-white">Welcome</h2>
          <p className="text-slate-600 dark:text-slate-400 mt-2">Sign in to the dashboard.</p>
        </div>
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-semibold text-slate-600 dark:text-slate-300 mb-2">Email</label>
            <input
              type="email"
              value={credentials.email}
              onChange={(e) => setCredentials({...credentials, email: e.target.value})}
              className="w-full px-4 py-3 bg-transparent border border-slate-300 dark:border-slate-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all-300"
              placeholder="admin@university.edu"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-semibold text-slate-600 dark:text-slate-300 mb-2">Password</label>
            <input
              type="password"
              value={credentials.password}
              onChange={(e) => setCredentials({...credentials, password: e.target.value})}
              className="w-full px-4 py-3 bg-transparent border border-slate-300 dark:border-slate-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all-300"
              placeholder="admin123"
              required
            />
          </div>
          {error && <div className="text-red-500 text-sm text-center bg-red-500/10 p-2 rounded-lg">{error}</div>}
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white py-3 rounded-lg font-semibold shadow-lg hover:shadow-xl hover:scale-105 transform disabled:opacity-50 disabled:cursor-not-allowed transition-all-300"
          >
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>
        <div className="mt-6 text-center text-xs text-slate-500 dark:text-slate-400">
          Demo: admin@university.edu / admin123
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
